import Logo from './logo';
import Menu from './menu';

export default ({ currentUser }) => {
  return (
    <header>
      <Logo currentUser={currentUser} className='logo' />
      <Menu currentUser={currentUser} className='menu' />
      <style jsx>{`
        header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin: 1em auto;
          max-width: 90%;

          .logo {
            flex: 1 1;
          }
          .menu {
            text-transform: uppercase;
          }
        }
      `}</style>
    </header>
  );
};
