import React from 'react';
import styled from 'styled-components';
import { device } from '../styles/device';
import theme from '../styles/theme';

import subHero1 from '../../public/subHero1.jpg';
import subHero2 from '../../public/subHero2.jpg';
import subHero3 from '../../public/subHero3.jpg';
import subHero4 from '../../public/subHero4.jpg';

const { colors, fontSizes } = theme;

const StyledGallery = styled.section`
  margin: 5rem auto;
  max-width: 90vw;

  @media ${device.mobileL} {
    max-width: 90%;
    margin: 3rem auto;
  }
`;

const StyledShowcase = styled.div`
  display: flex;
  flex-wrap: nowrap;
  flex-direction: row;
  justify-content: space-around;
  align-items: center;

  > * {
    margin: 0 0.4rem;
  }
`;

const StyledImgBlock = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  padding-bottom: 5rem;
  background-color: ${colors.lightText};
  height: 23rem;
  text-align: left;
  margin-left: 0.05rem;
  p,
  h3 {
    color: ${colors.white};
  }

  h3 {
    font-size: ${fontSizes.big};
  }

  p {
    font-size: ${fontSizes.normal};
    font-weight: 400;
  }
`;

const StyledImgInner = styled.div`
  background-color: ${colors.darkText};
  height
`;

const StyledImgBlockHead = styled.div`
  margin-bottom: ${theme.mbSm};
`;

const LandingGallery = () => {
  return (
    <StyledGallery>
      <StyledShowcase>
        <StyledImgBlock>
          <StyledImgInner>
            <StyledImgBlockHead>
              <h3>Concert 1</h3>
            </StyledImgBlockHead>
            <div className='block-1__info'>
              <p>Lorem ipsum dolor sit amet consectetur adipisicing elit.</p>
            </div>
          </StyledImgInner>
        </StyledImgBlock>

        <div className='block-2'>
          <div className='block-2__heading'>
            <h3>Concert 2</h3>
          </div>
          <div className='block-2__info'>
            Lorem ipsum dolor sit amet consectetur adipisicing elit.
          </div>
        </div>
        <div className='block-3'>
          <div className='block-3__heading'>
            <h3>Concert 3</h3>
          </div>
          <div className='block-3__info'>
            Lorem ipsum dolor sit amet consectetur adipisicing elit.
          </div>
        </div>
        <div className='block-4'>
          <div className='block-4__heading'>
            <h3>Concert 4</h3>
          </div>
          <div className='block-4__info'>
            Lorem ipsum dolor sit amet consectetur adipisicing elit.
          </div>
        </div>
      </StyledShowcase>
    </StyledGallery>
  );
};

export default LandingGallery;
