import IconFanprotect from '../icons/fanprotect';
import theme from '../styles/theme';
import { device } from '../styles/device';
import styled from 'styled-components';
import IconCode from '../icons/code';
import IconBuy from '../icons/buy';
import IconGuarantee from '../icons/guarantee';
import IconApprove from '../icons/approve';

const { colors, fontSizes } = theme;

const StyledFooter = styled.footer`
  display: flex;
  flex-direction: column;
  width: 90%;
  margin: 3rem auto;
`;

const StyledFooterMain = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;

const StyledMainIcons = styled.div`
  flex: 0 0 50%;
`;

const StyledIconRow = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  margin-top: 5px;
  svg {
    margin: 0 7px;
    color: ${colors.purple};
  }
  p {
    font-w
  }
`;

const FooterMenuLeft = styled.div`
  flex: 0 0 25%;
`;

const FooterMenuRight = styled.div`
  flex: 0 0 25%;
`;

const StyledCopyright = styled.section`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 1.5rem;
`;

const StyledCopyrightText = styled.div`
  svg {
    width: 18px;
    height: 18px;
    margin-top: -2px;
    color: ${colors.purple};
  }
`;

export default () => {
  return (
    <StyledFooter>
      <StyledFooterMain>
        <StyledMainIcons>
          <IconFanprotect />
          <StyledIconRow>
            <IconBuy />
            <p>Buy and sell with confidence</p>
          </StyledIconRow>
          <StyledIconRow>
            <IconApprove />
            <p>Customer service from beginning to your seat</p>
          </StyledIconRow>
          <StyledIconRow>
            <IconGuarantee />
            <p>Each order with 100% guaranty</p>
          </StyledIconRow>
        </StyledMainIcons>
        <FooterMenuLeft>
          <p>Get more info</p>
          <ul>
            <li>Why choose us</li>
            <li>How does it work</li>
            <li>Quick steps</li>
            <li>FAQ</li>
            <li>Visit our blog</li>
            <li>See all Plans</li>
          </ul>
        </FooterMenuLeft>
        <FooterMenuRight>
          <p>Quick Links</p>
          <ul>
            <li>All Categories</li>
            <li>Privacy Policy</li>
            <li>Carrers</li>
            <li>Terms of Use</li>
            <li>Support Desk</li>
          </ul>
        </FooterMenuRight>
      </StyledFooterMain>

      <StyledCopyright>
        <StyledCopyrightText>
          Â© {new Date().getFullYear()} Ticket Market. <IconCode /> by Piotr N.
        </StyledCopyrightText>
      </StyledCopyright>
    </StyledFooter>
  );
};
