import React, { useState } from 'react';
import styled from 'styled-components';
import theme from '../styles/theme';
import { device } from '../styles/device';
import IconArrow from '../icons/arrow';

const { colors, fontSizes } = theme;

const StyledSection = styled.section`
  min-height: 50vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 5rem;
  background-color: ${colors.transPurple};
  border-bottom: ${colors.shadowWhite};
`;
const StyledInnerSection = styled.div`
  max-width: 70%;
  border: 3px solid ${colors.purple};
  border-radius: 10px;
  padding: 1rem 3rem;
`;
const StyledHeader = styled.div`
  text-align: center;
  h1 {
    font-size: ${fontSizes.big};
    color: ${colors.purple};
    font-weight: 600;
  }
`;
const StyledRow = styled.div`
  display: flex;
  flex-direction: column;
  margin-left: 0.7rem;
  margin-right: 0.7rem;
  &:not(:last-child) {
    margin-bottom: 1rem;
  }
`;
const StyledQuestion = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 0.5rem;
  p {
    font-size: ${fontSizes.large};
    font-weight: 600;
    color: ${colors.darkText};
  }
  span {
    font-size: ${fontSizes.Xlarge};
    color: ${colors.purple};
    font-weight: 700;
  }
`;
const StyledIconFirst = styled.button`
  height: auto;
  width: 20px;
  color: ${colors.purple};
  cursor: pointer;
  background-color: transparent;
  border-radius: 100px;
  transform: rotateX(180deg);
  overflow: hidden;
  transform: ${(props) => (props.open ? `rotateX(0deg)` : '')};
`;
const StyledIconSecond = styled.button`
  height: auto;
  width: 20px;
  color: ${colors.purple};
  cursor: pointer;
  background-color: transparent;
  border-radius: 100px;
  transform: rotateX(180deg);
  overflow: hidden;
  transform: ${(props) => (props.openSecond ? `rotateX(0deg)` : '')};
`;
const StyledIconThird = styled.button`
  height: auto;
  width: 20px;
  color: ${colors.purple};
  cursor: pointer;
  background-color: transparent;
  border-radius: 100px;
  transform: rotateX(180deg);
  overflow: hidden;
  transform: ${(props) => (props.openThird ? `rotateX(0deg)` : '')};
`;
const StyledIconFourth = styled.button`
  height: auto;
  width: 20px;
  color: ${colors.purple};
  cursor: pointer;
  background-color: transparent;
  border-radius: 100px;
  transform: rotateX(180deg);
  overflow: hidden;
  transform: ${(props) => (props.openFourth ? `rotateX(0deg)` : '')};
`;
const StyledIconFifth = styled.button`
  height: auto;
  width: 20px;
  color: ${colors.purple};
  cursor: pointer;
  background-color: transparent;
  border-radius: 100px;
  transform: rotateX(180deg);
  overflow: hidden;
  transform: ${(props) => (props.openFifth ? `rotateX(0deg)` : '')};
`;

const StyledAnswerFirst = styled.div`
  width: 90%;
  margin-left: 0.5rem;
  max-height: ${(props) => (props.open ? '0' : '3rem')};
  opacity: ${(props) => (props.open ? '0' : '1')};
  transition: all 0.1s ease-in;
  overflow: hidden;
  p {
    font-size: ${fontSizes.normal};
    font-weight: 500;
    color: ${colors.darkText};
  }
  span {
    color: ${colors.purple};
    font-weight: 700;
  }
`;

const StyledAnswerSecond = styled.div`
  width: 90%;
  margin-left: 0.5rem;
  max-height: ${(props) => (props.openSecond ? '0' : '3rem')};
  opacity: ${(props) => (props.openSecond ? '0' : '1')};
  transition: all 0.1s ease-in;
  overflow: hidden;
  p {
    font-size: ${fontSizes.normal};
    font-weight: 500;
    color: ${colors.darkText};
  }
  span {
    color: ${colors.purple};
    font-weight: 700;
  }
`;
const StyledAnswerThird = styled.div`
  width: 90%;
  margin-left: 0.5rem;
  max-height: ${(props) => (props.openThird ? '0' : '3rem')};
  opacity: ${(props) => (props.openThird ? '0' : '1')};
  transition: all 0.1s ease-in;
  overflow: hidden;
  p {
    font-size: ${fontSizes.normal};
    font-weight: 500;
    color: ${colors.darkText};
  }
  span {
    color: ${colors.purple};
    font-weight: 700;
  }
`;
const StyledAnswerFourth = styled.div`
  width: 90%;
  margin-left: 0.5rem;
  max-height: ${(props) => (props.openFourth ? '0' : '3rem')};
  opacity: ${(props) => (props.openFourth ? '0' : '1')};
  transition: all 0.1s ease-in;
  overflow: hidden;
  p {
    font-size: ${fontSizes.normal};
    font-weight: 500;
    color: ${colors.darkText};
  }
  span {
    color: ${colors.purple};
    font-weight: 700;
  }
`;
const StyledAnswerFifth = styled.div`
  width: 90%;
  margin-left: 0.5rem;
  max-height: ${(props) => (props.openFifth ? '0' : '3rem')};
  opacity: ${(props) => (props.openFifth ? '0' : '1')};
  transition: all 0.1s ease-in;
  overflow: hidden;
  p {
    font-size: ${fontSizes.normal};
    font-weight: 500;
    color: ${colors.darkText};
  }
  span {
    color: ${colors.purple};
    font-weight: 700;
  }
`;

const QuestionsSection = () => {
  const [open, setOpen] = useState(true);
  const [openSecond, setOpenSec] = useState(true);
  const [openThird, setOpenThd] = useState(true);
  const [openFourth, setOpenFrth] = useState(true);
  const [openFifth, setOpenFth] = useState(true);

  const handleClick = () => {
    setOpen(!open);
  };
  const handleClickSec = () => {
    setOpenSec(!openSecond);
  };
  const handleClickThird = () => {
    setOpenThd(!openThird);
  };
  const handleClickFourth = () => {
    setOpenFrth(!openFourth);
  };
  const handleClickFifth = () => {
    setOpenFth(!openFifth);
  };

  return (
    <StyledSection>
      <StyledInnerSection>
        <StyledHeader>
          <h1>Frequently Asked Questions</h1>
        </StyledHeader>
        <StyledRow>
          <StyledQuestion>
            <p>
              <span>Q</span>: How can I sell my ticket?
            </p>
            <StyledIconFirst onClick={handleClick} open={open}>
              <IconArrow />
            </StyledIconFirst>
          </StyledQuestion>
          <StyledAnswerFirst open={open}>
            <p>
              <span>A</span>: That's really easy! Sign up to a new account, in the right corner find the 'New Ticket' option. It will than redirect you to a new window where you can create your first ticket.
            </p>
          </StyledAnswerFirst>
        </StyledRow>
        <StyledRow>
          <StyledQuestion>
            <p>
              <span>Q</span>: How do Ticket Market handle the payments?
            </p>
            <StyledIconSecond onClick={handleClickSec} openSecond={openSecond}>
              <IconArrow />
            </StyledIconSecond>
          </StyledQuestion>
          <StyledAnswerSecond openSecond={openSecond}>
            <p>
              <span>A</span>: The payments are done trough.
            </p>
          </StyledAnswerSecond>
        </StyledRow>
        <StyledRow>
