import React, { useState } from 'react';
import styled from 'styled-components';
import theme from '../styles/theme';
import { device } from '../styles/device';
import IconArrow from '../icons/arrow';

const { colors, fontSizes } = theme;

const StyledSection = styled.section`
  min-height: 50vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 5rem;
  background-color: ${colors.transPurple};
  border-bottom: ${colors.shadowWhite};
`;

const StyledInnerSection = styled.div`
  min-width: 70%;
  border: 3px solid ${colors.purple};
  border-radius: 10px;
  padding: 1rem 3rem;
`;

const StyledHeader = styled.div`
  text-align: center;
  h1 {
    font-size: ${fontSizes.big};
    color: ${colors.purple};
    font-weight: 600;
  }
`;

const StyledRow = styled.div`
  display: flex;
  flex-direction: column;
  margin-left: 0.7rem;
  margin-right: 0.7rem;
  &:not(:last-child) {
    margin-bottom: 1rem;
  }
`;

const StyledQuestion = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 0.5rem;
  p {
    font-size: ${fontSizes.large};
    font-weight: 600;
    color: ${colors.darkText};
  }
  span {
    font-size: ${fontSizes.Xlarge};
    color: ${colors.purple};
    font-weight: 700;
  }
`;

const StyledIcon = styled.button`
  height: auto;
  width: 20px;
  color: ${colors.purple};
  cursor: pointer;
  background-color: transparent;
  border-radius: 100px;
  svg {
    transform: ${(props) => (props.open ? 'rotateX(-180deg)' : '')};
  }
`;

const StyledAnswer = styled.div`
  width: 90%;
  margin-left: 0.5rem;
  max-height: ${(props) => (props.open ? '0' : '3rem')};
  opacity: ${(props) => (props.open ? '0' : '1')};
  transition: all 0.1s ease-in;
  over

  p {
    font-size: ${fontSizes.normal};
    font-weight: 500;
    color: ${colors.darkText};
  }
  span {
    color: ${colors.purple};
    font-weight: 700;
  }
`;

const QuestionsSection = () => {
  const [open, setOpen] = useState(false);

  const handleClick = () => {
    setOpen(!open);
  };

  return (
    <StyledSection>
      <StyledInnerSection>
        <StyledHeader>
          <h1>Frequently Asked Questions</h1>
        </StyledHeader>
        <StyledRow>
          <StyledQuestion>
            <p>
              <span>Q</span>: Lorem ipsum, dolor sit amet consectetur
              adipisicing elit.
            </p>
            <StyledIcon onClick={handleClick}>
              <IconArrow />
            </StyledIcon>
          </StyledQuestion>
          <StyledAnswer open={open}>
            <p>
              <span>A</span>: Lorem ipsum dolor sit amet consectetur adipisicing
              elit. Reiciendis voluptatum beatae magni.
            </p>
          </StyledAnswer>
        </StyledRow>
        <StyledRow>
          <StyledQuestion>
            <p>
              <span>Q</span>: Lorem ipsum, dolor sit amet consectetur
              adipisicing elit.
            </p>
            <StyledIcon>
              <IconArrow />
            </StyledIcon>
          </StyledQuestion>
          <StyledAnswer id='second-answer'>
            <p>
              <span>A</span>: Lorem ipsum dolor sit amet consectetur adipisicing
              elit. Reiciendis voluptatum beatae magni.
            </p>
          </StyledAnswer>
        </StyledRow>
        <StyledRow>
          <StyledQuestion>
            <p>
              <span>Q</span>: Lorem ipsum, dolor sit amet consectetur
              adipisicing elit.
            </p>
            <StyledIcon>
              <IconArrow />
            </StyledIcon>
          </StyledQuestion>
          <StyledAnswer id='third-answer'>
            <p>
              <span>A</span>: Lorem ipsum dolor sit amet consectetur adipisicing
              elit. Reiciendis voluptatum beatae magni.
            </p>
          </StyledAnswer>
        </StyledRow>
        <StyledRow>
          <StyledQuestion>
            <p>
              <span>Q</span>: Lorem ipsum, dolor sit amet consectetur
              adipisicing elit.
            </p>
            <StyledIcon>
              <IconArrow />
            </StyledIcon>
          </StyledQuestion>
          <StyledAnswer id='fourth-answer'>
            <p>
              <span>A</span>: Lorem ipsum dolor sit amet consectetur adipisicing
              elit. Reiciendis voluptatum beatae magni.
            </p>
          </StyledAnswer>
        </StyledRow>
        <StyledRow>
          <StyledQuestion>
            <p>
              <span>Q</span>: Lorem ipsum, dolor sit amet consectetur
              adipisicing elit.
            </p>
            <StyledIcon>
              <IconArrow />
            </StyledIcon>
          </StyledQuestion>
          <StyledAnswer id='fifth-answer'>
            <p>
              <span>A</span>: Lorem ipsum dolor sit amet consectetur adipisicing
              elit. Reiciendis voluptatum beatae magni.
            </p>
          </StyledAnswer>
        </StyledRow>
      </StyledInnerSection>
    </StyledSection>
  );
};

export default QuestionsSection;
