import React from 'react';
import Link from 'next/link';
import styled from 'styled-components';
import heroMobile2 from '../../public/heroMobile2.jpg';
import { device } from '../styles/device';
import theme from '../styles/theme';
import bgVideo from '../../public/bgVideo.mp4';
import SearchBox from '../searchbox';

const { colors, fontSizes } = theme;

const StyledHero = styled.div`
  display: flex;
  align-items: ${props => props.just}
  position: relative;
  width: 100%;
  height: 85vh;

  @media ${device.tablet} {
    background-image: linear-gradient(
        to top right,
        rgba(26, 25, 28, 0.1),
        rgba(252, 250, 255, 0.1)
      ),
      url(${heroMobile2});
    background-size: cover;
    background-position: center;
    opacity: 0.8;
  }
`;

const StyledVideoContent = styled.video`
  height: 100%;
  width: 100%;
  object-fit: cover;
  @media ${device.tablet} {
    display: none;
  }
`;

const StyledVideo = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: 100%;
  z-index: -1;
  opacity: 0.8;
  background-color: ${colors.transPurple};
  overflow: hidden;
`;

const StyledHeroSection = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  @media ${device.tablet} {
    padding: 0;
    margin: 0 1.5rem;
  }
`;

const StyledHeroHeading = styled.div`
  text-transform: uppercase;
  h1 {
    color: ${colors.white};
    font-weight: 800;
    letter-spacing: 0.5px;
    font-size: ${fontSizes.Xbig};
    margin-bottom: 0 auto ${theme.mbSm} auto;
  }
`;

const StyledButton = styled.a`
  margin-left: 0.2rem;
  padding: 0.75rem 3.5rem;
  border-radius: 100px;
  border: 2px solid ${colors.white};
  color: ${colors.white};
  background-color: transparent;
  font-size: ${fontSizes.normal};
  font-weight: 500;
  transition: all 0.2s;
  :hover {
    background-color: ${colors.white};
    color: ${colors.purple};
  }
`;

const HeroSection = () => {
  return (
    <StyledHero>
      <StyledVideo>
        <StyledVideoContent src={bgVideo} autoPlay loop>
          Your browser is not supported!
        </StyledVideoContent>
      </StyledVideo>

      <StyledHeroSection>
        <StyledHeroHeading>
          <h1>Don't miss the latest events!</h1>
        </StyledHeroHeading>
        <SearchBox />

        <Link href='auth/signup'>
          <StyledButton>Make an account</StyledButton>
        </Link>
      </StyledHeroSection>
    </StyledHero>
  );
};

export default HeroSection;
