import { device } from '../components/styles/device';
import theme from '../components/styles/theme';

const { colors, fontSizes } = theme;

const StyledHero = styled.div`
  display: flex;
  align-items: center;
  width: 100%;
  height: 85vh;
  background-image: linear-gradient(
      to top right,
      rgba(26, 25, 28, 0.1),
      rgba(252, 250, 255, 0.1)
    ),
    url(${landingHero});
  background-size: cover;
  background-position: center;
  -webkit-clip-path: polygon(0 0, 100% 0, 100% 75vh, 0 100%);
  clip-path: polygon(0 0, 100% 0, 100% 75vh, 0 100%);

  @media ${device.tablet} {
    background-image: url(${landingHeroMobile});
  }
`;

const StyledHeroSection = styled.div`
  padding-left: 4rem;
  @media ${device.tablet} {
    padding: 0;
    margin: 0 1.5rem;
  }
`;

const StyledHeroHeading = styled.div`
  text-transform: uppercase;
  h1 {
    color: ${colors.white};
    font-weight: 800;
    letter-spacing: 0.5px;
    font-size: ${fontSizes.Xbig};
    margin-bottom: 0 auto ${theme.mbSm} auto;
  }
`;

const StyledHeroSubHeading = styled.div`
  color: ${colors.white};
  font-weight: 600;
  p {
    font-size: ${fontSizes.large};
