import Link from 'next/link';
import IconSignup from './icons/signup';
import paths from '../paths';
import styled from 'styled-components';
import theme from './styles/theme';

const { colors, fontSizes } = theme;

const StyledList = styled.ul`
  display: flex;
  flex-direction: row;
  align-items: center;

  li {
    display: flex;
    align-items: center;
    padding: 15px 10px;
    font-size: ${fontSizes.large};
    font-weight: 500;
    cursor: pointer;

    span {
      display: flex;
      align-items: center
    }

    svg {
      height: 1.2rem;
      width: auto;
      margin-left: 0.5rem;  
    }

    :hover,
    :active {
      color: ${colors.purple};
    }
  }
`;

const StyledListPoint = styled.li``;

const StyledAnchor = styled.a``;

const StyledIcon = styled.div``;

const { signin, signup, orders, signout, ticketNew } = paths;

export default ({ currentUser }) => {
  const links = [
    !currentUser && {
      label: 'Sign Up',
      href: `${signup}`,
      icon: <IconSignup />,
    },
    !currentUser && { label: 'Sign In', href: `${signin}` },
    currentUser && { label: 'Sell tickets', href: `${ticketNew}` },
    currentUser && { label: 'My Orders', href: `${orders}` },
    currentUser && { label: 'Sign Out', href: `${signout}` },
  ]
    .filter((linkConfig) => linkConfig) // filter which are falsy
    .map(({ label, href, icon }) => {
      return (
        <StyledListPoint key={href}>
          <Link href={href}>
            <span>
              {label}
              {icon}
            </span>
          </Link>
        </StyledListPoint>
      );
    });

  return (
    <StyledList>
      {links}
      <StyledIcon>
        <IconSignup />
      </StyledIcon>
    </StyledList>
  );
};
