import Link from 'next/link';
import IconSignup from './icons/signup';
import paths from '../paths';
import styled from 'styled-components';
import theme from './styles/theme';

const { colors } = theme;

const StyledList = styled.ul`
  display: flex;
  flex-direction: row;
  align-items: center;

  li {
    padding: 15px 10px;
    font-

    :hover,
    :active {
      color: ${colors.purple};
    }
  }
`;

const StyledListPoint = styled.li``;

const StyledAnchor = styled.a``;

const StyledIcon = styled.div``;

const { signin, signup, orders, signout, ticketNew } = paths;

export default ({ currentUser }) => {
  const links = [
    !currentUser && { label: 'Sign Up', href: `${signup}` },
    !currentUser && { label: 'Sign In', href: `${signin}` },
    currentUser && { label: 'Sell tickets', href: `${ticketNew}` },
    currentUser && { label: 'My Orders', href: `${orders}` },
    currentUser && { label: 'Sign Out', href: `${signout}` },
  ]
    .filter((linkConfig) => linkConfig) // filter which are falsy
    .map(({ label, href }) => {
      return (
        <StyledListPoint key={href}>
          <Link href={href}>
            <span>{label}</span>
          </Link>
        </StyledListPoint>
      );
    });

  return (
    <StyledList>
      {links}
      <StyledIcon>
        <IconSignup />
      </StyledIcon>
    </StyledList>
  );
};
