import React from 'react';
import styled from 'styled-components';
import theme from '../styles/theme';
import { device } from '../styles/device';
import IconArrow from '../icons/arrow';

const { colors, fontSizes } = theme;

const StyledSection = styled.section`
  min-height: 50vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 5rem;
  background-color: ${colors.transPurple};
  border-bottom: ${colors.shadowWhite};
`;

const StyledInnerSection = styled.div`
  min-width: 70%;
  border: 3px solid ${colors.purple};
  border-radius: 10px;
  padding: 1rem 3rem;
`;

const StyledHeader = styled.div`
  text-align: center;
  h1 {
    font-size: ${fontSizes.big};
    color: ${colors.purple};
    font-weight: 600;
  }
`;

const StyledRow = styled.div`
  display: flex;
  flex-direction: column;
  margin-left: 0.7rem;
  margin-right: 0.7rem;
  &:not(:last-child) {
    margin-bottom: 1rem;
  }
`;

const StyledQuestion = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 0.5rem;
  p {
    font-size: ${fontSizes.large};
    font-weight: 500;
    color: ${colors.darkText};
  }
  span {
    font-size: ${fontSizes.Xlarge};
    color: ${colors.purple};
    font-weight: 700;
  }
`;

const StyledIcon = styled.div`
  height: auto;
  width: 20px;
  color: ${colors.purple};
  cursor: pointer;
`;

const StyledAnswer = styled.div`
  width: 90%;
  margin-left: 0.5rem;
  /* display: none; */

  p {
    font-size: ${fontSizes.normal};
    font-weight: 500;
    color: ${colors.lightText};
  }
  span {
    color: ${colors.purple};
    font-weight: 700;
  }
`;

const QuestionsSection = () => {
  const firstQuestionHandle = () => {
    var x = document.getElementById('first-answer');
    if (x.styles.display === 'none') {
      x.styles.display === 'block';
    } else {
      x.styles.display === 'none';
    }
  };
  const secondQuestionHandle = () => {
    var x = document.getElementById('first-answer');
    if (x.styles.display === 'none') {
      x.styles.display === 'block';
    } else {
      x.styles.display === 'none';
    }
  };
  const thirdQuestionHandle = () => {
    var x = document.getElementById('first-answer');
    if (x.styles.display === 'none') {
      x.styles.display === 'block';
    } else {
      x.styles.display === 'none';
    }
  };
  const fourthQuestionHandle = () => {
    var x = document.getElementById('first-answer');
    if (x.styles.display === 'none') {
      x.styles.display === 'block';
    } else {
      x.styles.display === 'none';
    }
  };
  const fifthQuestionHandle = () => {
    var x = document.getElementById('first-answer');
    if (x.styles.display === 'none') {
      x.styles.display === 'block';
    } else {
      x.styles.display === 'none';
    }
  };

  return (
    <StyledSection>
      <StyledInnerSection>
        <StyledHeader>
          <h1>Frequently Asked Questions</h1>
        </StyledHeader>
        <StyledRow>
          <StyledQuestion>
            <p>
              <span>Q</span>: Lorem ipsum, dolor sit amet consectetur
              adipisicing elit.
            </p>

            <StyledIcon>
              <IconArrow onClick={(e) => firstQuestionHandle()} />
            </StyledIcon>
          </StyledQuestion>

          <StyledAnswer id='first-answer'>
            <p>
              <span>A</span>: Lorem ipsum dolor sit amet consectetur adipisicing
              elit. Reiciendis voluptatum beatae magni.
            </p>
          </StyledAnswer>
        </StyledRow>
        <StyledRow>
          <StyledQuestion>
            <p>
              <span>Q</span>: Lorem ipsum, dolor sit amet consectetur
              adipisicing elit.
            </p>
            <StyledIcon>
              <IconArrow onClick={(e) => secondQuestionHandle()} />
            </StyledIcon>
          </StyledQuestion>

          <StyledAnswer id='second-answer'>
            <p>
              <span>A</span>: Lorem ipsum dolor sit amet consectetur adipisicing
              elit. Reiciendis voluptatum beatae magni.
            </p>
          </StyledAnswer>
        </StyledRow>
        <StyledRow>
          <StyledQuestion>
            <p>
              <span>Q</span>: Lorem ipsum, dolor sit amet consectetur
              adipisicing elit.
            </p>
            <StyledIcon>
              <IconArrow onClick={(e) => thirdQuestionHandle()} />
            </StyledIcon>
          </StyledQuestion>

          <StyledAnswer id='third-answer'>
            <p>
              <span>A</span>: Lorem ipsum dolor sit amet consectetur adipisicing
              elit. Reiciendis voluptatum beatae magni.
            </p>
          </StyledAnswer>
        </StyledRow>
        <StyledRow>
          <StyledQuestion>
            <p>
              <span>Q</span>: Lorem ipsum, dolor sit amet consectetur
              adipisicing elit.
            </p>
            <StyledIcon>
              <IconArrow onClick={(e) => fourthQuestionHandle()} />
            </StyledIcon>
          </StyledQuestion>

          <StyledAnswer id='fourth-answer'>
            <p>
              <span>A</span>: Lorem ipsum dolor sit amet consectetur adipisicing
              elit. Reiciendis voluptatum beatae magni.
            </p>
          </StyledAnswer>
        </StyledRow>
        <StyledRow>
          <StyledQuestion>
            <p>
              <span>Q</span>: Lorem ipsum, dolor sit amet consectetur
              adipisicing elit.
            </p>
            <StyledIcon>
              <IconArrow onClick={(e) => fifthQuestionHandle()} />
            </StyledIcon>
          </StyledQuestion>

          <StyledAnswer id='fifth-answer'>
            <p>
              <span>A</span>: Lorem ipsum dolor sit amet consectetur adipisicing
              elit. Reiciendis voluptatum beatae magni.
            </p>
          </StyledAnswer>
        </StyledRow>
      </StyledInnerSection>
    </StyledSection>
  );
};

export default QuestionsSection;
