    var x = document.getElementById('first-answer');
    if (x.styles.display === 'none') {
      x.styles.display === 'block';
    } else {
      x.styles.display === 'none';
    }
  };
  const fourthQuestionHandle = () => {
    var x = document.getElementById('first-answer');
    if (x.styles.display === 'none') {
      x.styles.display === 'block';
    } else {
      x.styles.display === 'none';
    }
  };
  const fifthQuestionHandle = () => {
    var x = document.getElementById('first-answer');
    if (x.styles.display === 'none') {
      x.styles.display === 'block';
    } else {
      x.styles.display === 'none';
    }
  };

  return (
    <StyledSection>
      <StyledInnerSection>
        <StyledHeader>
          <h1>Frequently Asked Questions</h1>
        </StyledHeader>
        <StyledRow>
          <StyledQuestion>
            <p>Lorem ipsum, dolor sit amet consectetur adipisicing elit.</p>

            <StyledIcon>
              <IconArrow onClick={(e) => firstQuestionHandle()} />
            </StyledIcon>
          </StyledQuestion>

          <StyledAnswer id='first-answer'>
            <p>
              Lorem ipsum dolor sit amet consectetur adipisicing elit.
              Reiciendis voluptatum beatae magni.
            </p>
          </StyledAnswer>
        </StyledRow>
        <StyledRow>
          <StyledQuestion>
            <p>Lorem ipsum, dolor sit amet consectetur adipisicing elit.</p>
          </StyledQuestion>
          <StyledIcon>
            <IconArrow onClick={(e) => secondQuestionHandle()} />
          </StyledIcon>
          <StyledAnswer id='second-answer'>
            <p>
              Lorem ipsum dolor sit amet consectetur adipisicing elit.
              Reiciendis voluptatum beatae magni.
            </p>
          </StyledAnswer>
        </StyledRow>
        <StyledRow>
          <StyledQuestion>
            <p>Lorem ipsum, dolor sit amet consectetur adipisicing elit.</p>
          </StyledQuestion>
          <StyledIcon>
            <IconArrow onClick={(e) => thirdQuestionHandle()} />
          </StyledIcon>
          <StyledAnswer id='third-answer'>
            <p>
              Lorem ipsum dolor sit amet consectetur adipisicing elit.
              Reiciendis voluptatum beatae magni.
            </p>
          </StyledAnswer>
        </StyledRow>
        <StyledRow>
          <StyledQuestion>
            <p>Lorem ipsum, dolor sit amet consectetur adipisicing elit.</p>
          </StyledQuestion>
          <StyledIcon>
            <IconArrow onClick={(e) => fourthQuestionHandle()} />
          </StyledIcon>
          <StyledAnswer id='fourth-answer'>
            <p>
              Lorem ipsum dolor sit amet consectetur adipisicing elit.
              Reiciendis voluptatum beatae magni.
            </p>
          </StyledAnswer>
        </StyledRow>
        <StyledRow>
          <StyledQuestion>
            <p>Lorem ipsum, dolor sit amet consectetur adipisicing elit.</p>
          </StyledQuestion>
          <StyledIcon>
            <IconArrow onClick={(e) => fifthQuestionHandle()} />
          </StyledIcon>
          <StyledAnswer id='fifth-answer'>
            <p>
              Lorem ipsum dolor sit amet consectetur adipisicing elit.
              Reiciendis voluptatum beatae magni.
            </p>
          </StyledAnswer>
        </StyledRow>
      </StyledInnerSection>
    </StyledSection>
  );
};

export default QuestionsSection;
